let graphQLOrderQuery="query cart {\n  order {\n    items {\n      id\n      __typename\n      ...BundleOrderItemFragment\n      ...FlightOrderItemFragment\n      ...HotelOrderItemFragment\n      ...SeatOrderItemFragment\n      ...TravelerAncillaryOrderItemFragment\n      ...VehicleOrderItemFragment\n      ...ItineraryAncillaryOrderItemFragment\n    }\n    travelers {\n      id\n      firstName\n      lastName\n      middleName\n      suffix\n      isPrimary\n      type\n      ssrs {\n        code\n        flightId\n        title\n        price {\n          amount\n          currency\n          __typename\n        }\n        additionalInfo\n        __typename\n      }\n      __typename\n    }\n    price {\n      total\n      balanceDue\n      taxes {\n        amount\n        __typename\n      }\n      fees {\n        amount\n        __typename\n      }\n      __typename\n    }\n    payments {\n      ... on PromoPayment {\n        id\n        description\n        total {\n          amount\n          currency\n          __typename\n        }\n        __typename\n      }\n      __typename\n    }\n    __typename\n  }\n}\n\nfragment BundleOrderItemFragment on OrderItem {\n  ... on BundleOrderItem {\n    id\n    bundle {\n      id\n      tier\n      name\n      banner\n      ancillaries {\n        name\n        type\n        price {\n          amount\n          __typename\n        }\n        __typename\n      }\n      __typename\n    }\n    price {\n      amount\n      currency\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment FlightOrderItemFragment on OrderItem {\n  ... on FlightOrderItem {\n    id\n    flight {\n      id\n      number\n      origin {\n        code\n        displayName\n        city\n        state\n        __typename\n      }\n      destination {\n        code\n        displayName\n        city\n        state\n        __typename\n      }\n      departingTime\n      arrivalTime\n      isOvernight\n      __typename\n    }\n    seatmap {\n      seatSizesMap {\n        id\n        name\n        __typename\n      }\n      __typename\n    }\n    flightPrice: price {\n      total\n      subtotal\n      taxesAndFees\n      taxes {\n        total {\n          amount\n          currency\n          __typename\n        }\n        breakdown {\n          name\n          code\n          value {\n            amount\n            currency\n            __typename\n          }\n          __typename\n        }\n        __typename\n      }\n      fees {\n        total {\n          amount\n          currency\n          __typename\n        }\n        breakdown {\n          name\n          code\n          value {\n            amount\n            currency\n            __typename\n          }\n          __typename\n        }\n        __typename\n      }\n      discountValue {\n        amount\n        currency\n        __typename\n      }\n      discountType\n      total\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment HotelOrderItemFragment on OrderItem {\n  ... on HotelOrderItem {\n    id\n    hotelPrice: price {\n      total\n      __typename\n    }\n    roomType\n    roomsCount\n    roomId\n    hotelId\n    hotel {\n      name\n      promos {\n        id\n        code\n        headlineDescription\n        __typename\n      }\n      __typename\n    }\n    checkin {\n      time\n      __typename\n    }\n    checkout {\n      time\n      __typename\n    }\n    roomsCount\n    adultCount\n    childrenCount\n    __typename\n  }\n  __typename\n}\n\nfragment SeatOrderItemFragment on OrderItem {\n  ... on SeatOrderItem {\n    id\n    flightId\n    travelerId\n    column\n    row\n    price {\n      amount\n      currency\n      __typename\n    }\n    bundledAncillaryPrice {\n      amount\n      __typename\n    }\n    isBundledAncillaryIncluded\n    seatSizeId\n    __typename\n  }\n  __typename\n}\n\nfragment TravelerAncillaryOrderItemFragment on OrderItem {\n  ... on TravelerAncillaryOrderItem {\n    id\n    flightId\n    travelerId\n    ancillaryType\n    quantity\n    price {\n      amount\n      currency\n      __typename\n    }\n    bundledAncillaryPrice {\n      amount\n      __typename\n    }\n    isBundledAncillaryIncluded\n    __typename\n  }\n  __typename\n}\n\nfragment VehicleOrderItemFragment on OrderItem {\n  ... on VehicleOrderItem {\n    id\n    vehiclePrice: price {\n      total {\n        amount\n        currency\n        __typename\n      }\n      __typename\n    }\n    vehicle {\n      category\n      code\n      type\n      description\n      __typename\n    }\n    vendor {\n      name\n      __typename\n    }\n    promotions {\n      id\n      code\n      headlineDescription\n      __typename\n    }\n    pickUpDate\n    dropOffDate\n    __typename\n  }\n  __typename\n}\n\nfragment ItineraryAncillaryOrderItemFragment on OrderItem {\n  ... on ItineraryAncillaryOrderItem {\n    id\n    ancillaryType\n    quantity\n    price {\n      amount\n      currency\n      __typename\n    }\n    bundledAncillaryPrice {\n      amount\n      __typename\n    }\n    isBundledAncillaryIncluded\n    __typename\n  }\n  __typename\n}\n",graphQLFlightSearchQuery="\nquery flightSearchCriteria {\n  flightSearchCriteria {\n    tripType\n    origin\n    destination\n    departDate\n    returnDate\n    adultsCount\n    lapInfantDobs\n    childrenDobs\n    lapInfantCount\n    childrenCount\n    __typename\n  }\n  order {\n    items {\n      id\n      ... on FlightOrderItem {\n        flight {\n          id\n          departingTime\n          __typename\n        }\n        __typename\n      }\n      __typename\n    }\n    __typename\n  }\n}\n\n",graphQLHotelQuery="\nquery hotels($packageHotelSearchCriteria: PackageHotelSearchInput, $sort: HotelSort, $filters: HotelFiltersInput, $offset: Int, $limit: Int, $origin: IataCode, $destination: IataCode, $departureDate: DateTime, $returnDate: DateTime) {\n  application(name: DESKTOPBOOKINGPATH) {\n    ... on DesktopBookingPath {\n      destinationAdverts(filters: {section: BOOKING_HOTELS, position: TOP, origin: $origin, destination: $destination, departureDate: $departureDate, returningDate: $returnDate}) {\n        content\n        position\n        __typename\n      }\n      __typename\n    }\n    __typename\n  }\n  hotelsForPackage(packageHotelSearchCriteria: $packageHotelSearchCriteria, filters: $filters) {\n    result {\n      list(sort: $sort, offset: $offset, limit: $limit) {\n        ...HotelFragment\n        __typename\n      }\n      totalCount\n      filters {\n        ratings {\n          name\n          count\n          __typename\n        }\n        priceRanges {\n          from\n          to\n          count\n          __typename\n        }\n        amenities {\n          name\n          count\n          __typename\n        }\n        landmarks {\n          name\n          count\n          __typename\n        }\n        neighborhoods {\n          name\n          count\n          __typename\n        }\n        __typename\n      }\n      lowestHotelPrice\n      minRooms\n      maxRooms\n      __typename\n    }\n    errors\n    __typename\n  }\n  packageHotelSearchCriteria {\n    locationCode\n    checkIn\n    checkOut\n    roomsCount\n    __typename\n  }\n}\n\nfragment HotelFragment on Hotel {\n  id\n  name\n  address\n  description\n  geoPoint {\n    latitude\n    longitude\n    __typename\n  }\n  image\n  hotelGallery {\n    path\n    alternativeText\n    __typename\n  }\n  inOrder\n  featured\n  rating\n  promos {\n    id\n    code\n    headlineDescription\n    shortDescription\n    details\n    termsAndConditions\n    reservationFrom\n    reservationTo\n    occupancyFrom\n    occupancyTo\n    blackoutDates {\n      from\n      to\n      __typename\n    }\n    __typename\n  }\n  prices {\n    lowestRoom\n    dailyAverageRate\n    strikeThroughRate\n    packageTotal {\n      amount\n      currency\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\n\n",graphQLPromoMutation="\nmutation addPromoPayment($payment: PromoPaymentInput!) {\naddPromoPayment(payment: $payment) {\n  order {\n    loyalty {\n      ...LoyaltyFragment\n      __typename\n    }\n    payments {\n      ...OrderPaymentFragment\n      ...PromoPaymentFragment\n      ...VoucherPaymentFragment\n      __typename\n    }\n    price {\n      balanceDue\n      __typename\n    }\n    __typename\n  }\n  errors\n  __typename\n}\n}\n\nfragment OrderPaymentFragment on OrderPayment {\npaymentType\npaymentStatus\npaymentMethod\ntotal {\n  amount\n  currency\n  __typename\n}\n__typename\n}\n\nfragment PromoPaymentFragment on PromoPayment {\nid\ndescription\nisAutoApplied\n__typename\n}\n\nfragment VoucherPaymentFragment on VoucherPayment {\nid\ntype\n__typename\n}\n\nfragment LoyaltyFragment on OrderLoyalty {\ntotalPoints\ntotalAmount {\n  amount\n  currency\n  __typename\n}\napplicableAmount {\n  amount\n  currency\n  __typename\n}\napplicablePoints\nrewardPoints\ndueAmount {\n  amount\n  __typename\n}\nbonusPoints\nenrolled\neligible\nbonusAndRewardPoints\ntotalDueAmount {\n  amount\n  currency\n  __typename\n}\n__typename\n}\n\n";